sp_widget.client_script:
api.controller=function($scope, $rootScope, $window, spUtil) {
  /* widget controller */
  var c = this;

	c.completeAssign = function(){
		c.server.get({
			action: 'completeAssign'
		}).then(function(r){
			console.log($scope.data);
			spUtil.update($scope);
		})
	}

	c.toNextAssign = function(){
		console.log($scope.data);
		$window.location.href="https://dev78456.service-now.com/training/?id=training_assignment&table="+$scope.data.table+"&sys_id="+$scope.data.nextAssign.sys_id;
	}

	c.toHome = function(){
		//console.log($scope.data);
		$window.location.href="https://dev78456.service-now.com/training/"
	}
};

